import { Address, Cell, beginCell, contractAddress } from "ton-core";
import { ConfigStore } from '../utils/ConfigStore';


const walletCode = Cell.fromBase64('');

export function walletV4FromConfig(walletConfig: string): { address: Address, publicKey: Buffer } {
    let store = new ConfigStore(walletConfig);
    let workchain = store.getInt('wc');
    let publicKey = store.getBuffer('pk');
    let walletId = store.getInt('walletId');

    let walletData = beginCell()
        .storeUint(0, 32)
        .storeUint(walletId, 32)
        .storeBuffer(publicKey)
        .storeBit(0)
        .endCell();

    let address = contractAddress(workchain, {
        code: walletCode,
        data: walletData,
    });

    return {
        address,
        publicKey,
    }
}